package Hotel.Management.System;import javax.swing.*;import java.awt.*;public class Splash extends JFrame {    Splash(){        setBackground(Color.BLACK);        ImageIcon imageIcon = new ImageIcon(ClassLoader.getSystemResource("icon/splash.jpg"));        Image image = imageIcon.getImage().getScaledInstance(800,600, Image.SCALE_DEFAULT);        ImageIcon imageIcon2 = new ImageIcon(image);        JLabel label = new JLabel(imageIcon2);        label.setBackground(Color.BLACK);        label.setBounds(0,0,700,650);        add(label);        setLayout(null);        setLocation(400,80);        setSize(700,650);        setVisible(true);        try {            Thread.sleep(3000);            new Login();            setVisible(false);        }catch (Exception e){            e.printStackTrace();        }    }    public static void main(String[] args) {        new Splash();    }}